// src/components/StoryDetail.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './StoryDetail.css';

function StoryDetail() {
  const { id } = useParams();
  const [story, setStory] = useState(null);
  const [activeTab, setActiveTab] = useState('overview');

  useEffect(() => {
    async function fetchStory() {
      try {
        const response = await axios.get(`http://localhost:5000/api/adventure/${id}`);
        setStory(response.data);
      } catch (error) {
        console.error('Error fetching story:', error);
      }
    }
    fetchStory();
  }, [id]);

  if (!story) return <div>Loading...</div>;

  return (
    <div className="story-detail">
      <img src={`https://ik.imagekit.io/dev24/${story.Image}`} alt={story.title} className="story-image" />
      <h2>{story.title}</h2>
      <div className="tabs">
        <button onClick={() => setActiveTab('overview')} className={activeTab === 'overview' ? 'active' : ''}>Overview</button>
        <button onClick={() => setActiveTab('details')} className={activeTab === 'details' ? 'active' : ''}>Details</button>
        <button onClick={() => setActiveTab('reviews')} className={activeTab === 'reviews' ? 'active' : ''}>Reviews</button>
      </div>
      <div className="tab-content">
        {activeTab === 'overview' && <div>{story.overview}</div>}
        {activeTab === 'details' && <div>{story.details}</div>}
        {activeTab === 'reviews' && <div>{story.reviews}</div>}
      </div>
    </div>
  );
}

export default StoryDetail;
