// src/components/StoryDetail.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './StoryDetail.css';

function StoryDetail() {
  const { id } = useParams();
  const [story, setStory] = useState(null);
  const [activeTab, setActiveTab] = useState('overview');

  useEffect(() => {
    async function fetchStory() {
      try {
        const response = await axios.get(`http://localhost:5000/api/adventure/${id}`);
        setStory(response.data);
      } catch (error) {
        console.error('Error fetching story:', error);
      }
    }
    fetchStory();
  }, [id]);

  if (!story) return <div>Loading...</div>;

  return (
    <div className="story-detail">
      <img
        src={`https://images.unsplash.com/photo-1613376023733-0a73315d9b06?w=500&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8MTR8fGFuaW1lJTIwY2FydG9vbnxlbnwwfHwwfHx8MA%3D%3D${story.Image}`}
        alt={story.title}
        className="story-image"
        onError={(e) => e.target.src='https://via.placeholder.com/600'}
      />
      <h2>{story.title}</h2>
      <div className="tabs">
        <button onClick={() => setActiveTab('overview')} className={activeTab === 'overview' ? 'active' : ''}>Overview</button>
        <button onClick={() => setActiveTab('details')} className={activeTab === 'details' ? 'active' : ''}>Details</button>
        <button onClick={() => setActiveTab('reviews')} className={activeTab === 'reviews' ? 'active' : ''}>Reviews</button>
      </div>
      <div className="tab-content">
        {activeTab === 'overview' && <div className="tab-text" dangerouslySetInnerHTML={{ __html: story.overview }} />}
        {activeTab === 'details' && <div className="tab-text" dangerouslySetInnerHTML={{ __html: story.details }} />}
        {activeTab === 'reviews' && <div className="tab-text" dangerouslySetInnerHTML={{ __html: story.reviews }} />}
      </div>
    </div>
  );
}

export default StoryDetail;
